mapper.select:
select subquery1.*
from
	(
	select a.*, b.userName , c.custName, d.soppTitle, e.custName ptncName, f.desc03 salesTypeN
	from swc_sales a
	left join swc_user b on a.userNo = b.userNo
	left join swc_cust c on a.custNo = c.custNo
	left join swc_sopp d on a.soppNo = d.soppNo
	left join swc_cust e on a.ptncNo = e.custNo
	left join swc_code f on a.salesType = f.codeNo
	where a.attrib not like 'XXX%' and a.compNo = #{compNo}

		and a.userNo = #{userNo}


		and a.soppNo = #{soppNo}


		and a.custNo = #{custNo}


		and a.salesType = #{salesType}


		and date_format(a.salesFrdatetime, '%Y-%m-%d') >= #{salesFrdatetime}


		and date_format(a.salesTodatetime, '%Y-%m-%d') <= #{salesTodatetime}


		and date_format(a.regDatetime, '%Y-%m-%d') >= #{regSDate}


		and date_format(a.regDatetime, '%Y-%m-%d') <= #{regEDate}


		and a.regDatetime Between #{listDateFrom} and now()

	) subquery1

where
	date_format(subquery1.regDatetime, '%Y-%m-%d') like concat('%',#{sSearch},'%')
	or subquery1.salesTitle like concat('%',#{sSearch},'%')
	or subquery1.salesFrdatetime like concat('%',#{sSearch},'%')
	or subquery1.salesTodatetime like concat('%',#{sSearch},'%')
	or subquery1.soppTitle like concat('%',#{sSearch},'%')
	or subquery1.custName like concat('%',#{sSearch},'%')
	or subquery1.userName like concat('%',#{sSearch},'%')
	or subquery1.ptncName like concat('%',#{sSearch},'%')
	or subquery1.salesDesc like concat('%',#{sSearch},'%')



	order by ${orderColumn} desc


	order by ${orderColumn} asc


limit #{limit} offset #{offset}

select count(*) from ( select subquery1.* from ( select a.*, b.userName , c.custName, d.soppTitle, e.custName ptncName, f.desc03 salesTypeN from swc_sales a left join swc_user b on a.userNo = b.userNo left join swc_cust c on a.custNo = c.custNo left join swc_sopp d on a.soppNo = d.soppNo left join swc_cust e on a.ptncNo = e.custNo left join swc_code f on a.salesType = f.codeNo where a.attrib not like 'XXX%' and a.compNo = #{compNo}
and a.userNo = #{userNo}
and a.soppNo = #{soppNo}
and a.custNo = #{custNo}
and a.salesType = #{salesType}
and date_format(a.salesFrdatetime, '%Y-%m-%d') >= #{salesFrdatetime}
and date_format(a.salesTodatetime, '%Y-%m-%d') <= #{salesTodatetime}
and date_format(a.regDatetime, '%Y-%m-%d') >= #{regSDate}
and date_format(a.regDatetime, '%Y-%m-%d') <= #{regEDate}
and a.regDatetime Between #{listDateFrom} and now()
) subquery1
where date_format(subquery1.regDatetime, '%Y-%m-%d') like concat('%',#{sSearch},'%') or subquery1.salesTitle like concat('%',#{sSearch},'%') or subquery1.salesFrdatetime like concat('%',#{sSearch},'%') or subquery1.salesTodatetime like concat('%',#{sSearch},'%') or subquery1.soppTitle like concat('%',#{sSearch},'%') or subquery1.custName like concat('%',#{sSearch},'%') or subquery1.userName like concat('%',#{sSearch},'%') or subquery1.ptncName like concat('%',#{sSearch},'%') or subquery1.salesDesc like concat('%',#{sSearch},'%')
order by ${orderColumn} desc
order by ${orderColumn} asc
) t

 		select a.*, b.userName , c.custName, d.soppTitle, e.custName ptncName, f.desc03 salesTypeN from swc_sales a  
		left join swc_user b on a.userNo = b.userNo
		left join swc_cust c on a.custNo = c.custNo
		left join swc_sopp d on a.soppNo = d.soppNo
		left join swc_cust e on a.ptncNo = e.custNo
		left join swc_code f on a.salesType = f.codeNo
		where a.attrib not like 'XXX%' and a.compNo = #{compNo}

 		 	and a.userNo = #{userNo}


 		 	and a.soppNo = #{soppNo}


 		 	and a.custNo = #{custNo}


 		 	and a.salesType = #{salesType}


 		 	and date_format(a.salesFrdatetime, '%Y-%m-%d') >= #{salesFrdatetime}


 			and date_format(a.salesTodatetime, '%Y-%m-%d') <= #{salesTodatetime}

		order by salesFrdatetime desc

mapper.update:
update swc_sales 
set 
 salesDesc=#{salesDesc}
,salesFrdatetime=#{salesFrdatetime}
,salesTodatetime=#{salesTodatetime}
,salesPlace=#{salesPlace}
,salesType=#{salesType}
,salesTitle=#{salesTitle}


		 ,soppNo= NULL


		 ,soppNo=#{soppNo}




		 ,custNo= NULL


		 ,custNo=#{custNo}




		 ,ptncNo= NULL


		 ,ptncNo=#{ptncNo}


,modDatetime = now()
where salesNo=#{salesNo}

mapper.delete:
update swc_sales 
set attrib = 'XXXXX'
where salesNo=#{salesNo}
