			(
			select a.schedNo, a.schedTitle, a.schedFrom, a.schedTo, a.schedPlace, a.schedDesc, a.regDatetime, b.custName, c.userName, d.desc03 schedTypeN, e.desc03 schedCatN
			from swc_sched a
			left join swc_cust b on a.custNo = b.custNo
