mapper.update:
update swc_user set 



		userPasswd=PASSWORD(#{userChangePasswd})


		userPasswd=PASSWORD(#{userPasswd})



	, listDateFrom = #{listDateFrom}


where userId=#{userId}
and userPasswd=PASSWORD(#{userPasswd})

update swc_user set userName = #{userName}, userRole = #{userRole}, userEmail = #{userEmail}, userTel = #{userTel}, userOtp=#{userOtp}, org_id = #{org_id}, userKey = #{userKey},
userPasswd = PASSWORD('1234'),
attrib = #{attrib} where userNo=#{userNo}

